import React, { useState } from 'react';
import { Link, useLocation } from 'react-router-dom';
import { Search, ShoppingBag, Menu, X, User, LogOut, ChevronDown } from 'lucide-react';
import { useApp } from '../context/AppContext';
import Footer from './Footer';

const Layout = ({ children }) => {
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const [showUserMenu, setShowUserMenu] = useState(false);
  const { cartItemsCount, user, setUser } = useApp();
  const location = useLocation();

  const toggleMobileMenu = () => {
    setIsMobileMenuOpen(!isMobileMenuOpen);
  };

  const handleSignOut = () => {
    setUser(null);
    setShowUserMenu(false);
  };

  const navLinks = [
    { name: 'Home', path: '/' },
    { name: 'Shop', path: '/shop' },
    { name: 'Collection', path: '/collection' },
    { name: 'About', path: '/about' },
    { name: 'Contact', path: '/contact' }
  ];

  return (
    <div className="layout">
      <header className="header">
        <div className="container">
          <div className="header-content">
            <Link to="/" className="logo">
              Clothing Store
            </Link>

            <nav className="nav">
              <ul className="nav-links">
                {navLinks.map((link) => (
                  <li key={link.name} className={link.hasDropdown ? 'dropdown' : ''}>
                    {link.hasDropdown ? (
                      <div 
                        className="dropdown-trigger"
                        onMouseEnter={() => setShowShopDropdown(true)}
                        onMouseLeave={() => setShowShopDropdown(false)}
                      >
                        <span className="nav-link">
                          {link.name}
                          <ChevronDown size={16} />
                        </span>
                        {showShopDropdown && (
                          <div className="dropdown-menu">
                            {link.dropdownItems.map((item) => (
                              <div key={item.name} className="dropdown-item">
                                {item.hasSubmenu ? (
                                  <div className="submenu-trigger">
                                    <span>{item.name}</span>
                                    <ChevronDown size={14} />
                                    <div className="submenu">
                                      {item.submenuItems.map((subItem) => (
                                        <Link 
                                          key={subItem.name} 
                                          to={subItem.path}
                                          className="submenu-item"
                                        >
                                          {subItem.name}
                                        </Link>
                                      ))}
                                    </div>
                                  </div>
                                ) : (
                                  <Link to={item.path} className="dropdown-link">
                                    {item.name}
                                  </Link>
                                )}
                              </div>
                            ))}
                          </div>
                        )}
                      </div>
                    ) : (
                      <Link 
                        to={link.path} 
                        className={location.pathname === link.path ? 'active' : ''}
                      >
                        {link.name}
                      </Link>
                    )}
                  </li>
                ))}
              </ul>
            </nav>

            <div className="header-actions">
              <form className="search-bar" onSubmit={handleSearch}>
                <input
                  type="text"
                  placeholder="Search products..."
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                />
                <button type="submit">
                  <Search size={18} />
                </button>
              </form>

              <div className="cart-icon">
                <Link to="/cart">
                  <ShoppingBag size={24} />
                  {cartItemsCount > 0 && (
                    <span className="cart-badge">{cartItemsCount}</span>
                  )}
                </Link>
              </div>

              {user ? (
                <div 
                  className="user-dropdown"
                  onMouseEnter={() => setShowAuthDropdown(true)}
                  onMouseLeave={() => setShowAuthDropdown(false)}
                >
                  <button className="user-btn">
                    <User size={24} />
                    <span>{user.name}</span>
                    <ChevronDown size={16} />
                  </button>
                  {showAuthDropdown && (
                    <div className="auth-dropdown">
                      <Link to="/profile" className="auth-link">
                        <User size={16} />
                        Profile
                      </Link>
                      <Link to="/orders" className="auth-link">
                        <ShoppingBag size={16} />
                        Orders
                      </Link>
                      <button onClick={handleSignOut} className="auth-link sign-out">
                        <LogOut size={16} />
                        Sign Out
                      </button>
                    </div>
                  )}
                </div>
              ) : (
                <div className="auth-buttons">
                  <Link to="/signin" className="btn btn-outline">
                    Sign In
                  </Link>
                  <Link to="/signup" className="btn btn-primary">
                    Sign Up
                  </Link>
                </div>
              )}

              <button className="mobile-menu-toggle" onClick={toggleMobileMenu}>
                {isMobileMenuOpen ? <X size={24} /> : <Menu size={24} />}
              </button>
            </div>
          </div>

          <div className={`mobile-nav ${isMobileMenuOpen ? 'active' : ''}`}>
            <ul className="mobile-nav-links">
              {navLinks.map((link) => (
                <li key={link.name}>
                  {link.hasDropdown ? (
                    <div className="mobile-dropdown">
                      <span className="mobile-dropdown-trigger">{link.name}</span>
                      <div className="mobile-dropdown-content">
                        {link.dropdownItems.map((item) => (
                          <div key={item.name}>
                            {item.hasSubmenu ? (
                              <div className="mobile-submenu">
                                <span className="mobile-submenu-trigger">{item.name}</span>
                                <div className="mobile-submenu-content">
                                  {item.submenuItems.map((subItem) => (
                                    <Link 
                                      key={subItem.name} 
                                      to={subItem.path}
                                      onClick={() => setIsMobileMenuOpen(false)}
                                    >
                                      {subItem.name}
                                    </Link>
                                  ))}
                                </div>
                              </div>
                            ) : (
                              <Link 
                                to={item.path} 
                                onClick={() => setIsMobileMenuOpen(false)}
                              >
                                {item.name}
                              </Link>
                            )}
                          </div>
                        ))}
                      </div>
                    </div>
                  ) : (
                    <Link 
                      to={link.path} 
                      onClick={() => setIsMobileMenuOpen(false)}
                      className={location.pathname === link.path ? 'active' : ''}
                    >
                      {link.name}
                    </Link>
                  )}
                </li>
              ))}
            </ul>
            <div className="mobile-auth">
              {user ? (
                <div className="mobile-user-info">
                  <p>Welcome, {user.name}!</p>
                  <button onClick={handleSignOut} className="btn btn-outline">
                    Sign Out
                  </button>
                </div>
              ) : (
                <div className="mobile-auth-buttons">
                  <Link to="/signin" className="btn btn-outline" onClick={() => setIsMobileMenuOpen(false)}>
                    Sign In
                  </Link>
                  <Link to="/signup" className="btn btn-primary" onClick={() => setIsMobileMenuOpen(false)}>
                    Sign Up
                  </Link>
                </div>
              )}
            </div>
          </div>
        </div>
      </header>

      <main className="main-content">
        {children}
      </main>

      <Footer />
    </div>
  );
};

export default Layout;
